	private $container;

	public function __construct(Customweb_Payment_Authorization_IPaymentMethod $paymentMethod, Customweb_DependencyInjection_IContainer $container){
		parent::__construct($paymentMethod);
		if (!($container instanceof Customweb_FirstDataConnect_Container)) {
			$container = new Customweb_FirstDataConnect_Container($container);
		}
		$this->container = $container;
	}

	/**
	 *
	 * @return Customweb_FirstDataConnect_Configuration
	 */
	protected function getGlobalConfiguration(){
		return $this->container->getConfiguration();
	}

	/**
	 *
	 * @return Customweb_FirstDataConnect_Container
	 */
	protected function getContainer(){
		return $this->container;
	}

	/**
	 *
	 * @see Customweb_Payment_Authorization_AbstractPaymentMethodWrapper::getPaymentInformationMap()
	 */
	protected function getPaymentInformationMap(){
		return self::$paymentMapping;
	}

	/**
	 * This method returns a list of form elements.
	 * This form elements are used to generate the user input.
	 * Sub classes may override this method to provide their own form fields.
	 *
	 * @return array List of form elements
	 */
	public function getFormFields(Customweb_Payment_Authorization_IOrderContext $orderContext, $aliasTransaction, $failedTransaction, $paymentCustomerContext, $authorizationMethod, $isMoto){
		return array();
	}

	/**
	 * This method returns the parameters to add for processing an authorization request for this payment method.
	 * Sub classes
	 * may override this method. But they should call the parent and merge in their own parameters.
	 *
	 * @param Customweb_FirstDataConnect_Authorization_Transaction $transaction
	 * @param array $formData
	 * @return array
	 */
	public function getAuthorizationParameters(Customweb_FirstDataConnect_Authorization_Transaction $transaction, array $formData, $authorizationMethod){
		$parameters = array();
		$parameters['paymentMethod'] = $this->getPaymentType();
		return $parameters;
	}

	public function getCapturingMode($transaction){
		if (!$this->existsPaymentMethodConfigurationValue('capturing') ||
				 ($transaction->getAuthorizationMethod() == Customweb_Payment_Authorization_Recurring_IAdapter::AUTHORIZATION_METHOD_NAME) ||
				 $transaction->getTransactionContext()->createRecurringAlias()) {
			return Customweb_FirstDataConnect_IConstants::OPERATION_SALE;
		}
		else {
			$capturingMode = $this->getPaymentMethodConfigurationValue('capturing');
		}
		if (strtolower($capturingMode) == 'deferred') {
			return Customweb_FirstDataConnect_IConstants::OPERATION_AUTHORISATION;
		}
		else {
			return Customweb_FirstDataConnect_IConstants::OPERATION_SALE;
		}
	}

	public function getAddressParameters(Customweb_Payment_Authorization_IOrderContext $orderContext, Customweb_Payment_Authorization_IPaymentCustomerContext $paymentCustomerContext, array $formData){
		$parameters = array();
		$parameters['mode'] = Customweb_FirstDataConnect_IConstants::DATA_PAYONLY;
		return array_merge_recursive($parameters, $this->getShippingParameters($orderContext, $paymentCustomerContext, $formData), 
				$this->getBillingParameters($orderContext, $paymentCustomerContext, $formData));
	}

	public function validate(Customweb_Payment_Authorization_IOrderContext $orderContext, Customweb_Payment_Authorization_IPaymentCustomerContext $paymentContext, array $formData){
		$this->preValidate($orderContext, $paymentContext);
	}

	protected function getBillingParameters(Customweb_Payment_Authorization_IOrderContext $orderContext, Customweb_Payment_Authorization_IPaymentCustomerContext $paymentCustomerContext, array $formData){
		$billing = $orderContext->getBillingAddress();
		$parameters = array();
		$name = trim($billing->getFirstName() . ' ' . $billing->getLastName());
		if (!empty($name)) {
			$parameters['bname'] = Customweb_Core_String::_($name)->substring(0, 96)->toString();
		}
		
		$street = trim($billing->getStreet());
		if (!empty($street)) {
			$parameters['baddr1'] = Customweb_Core_String::_($street)->substring(0, 96)->toString();
		}
		
		$city = trim($billing->getCity());
		if (!empty($city)) {
			$parameters['bcity'] = Customweb_Core_String::_($city)->substring(0, 96)->toString();
		}
		$state = trim($billing->getState());
		if (!empty($state)) {
			$parameters['bstate'] = Customweb_Core_String::_($state)->substring(0, 96)->toString();
		}
		$country = trim($billing->getCountryIsoCode());
		if (!empty($country)) {
			$parameters['bcountry'] = $country;
		}
		$zip = trim($billing->getPostCode());
		if (!empty($zip)) {
			$parameters['bzip'] = Customweb_Core_String::_($zip)->substring(0, 24)->toString();
		}
		
		$company = trim($billing->getCompanyName());
		if (!empty($company)) {
			$parameters['bcompany'] = $company;
		}
		
		$phone = trim($billing->getPhoneNumber());
		if (!empty($phone)) {
			$parameters['phone'] = Customweb_Core_String::_($phone)->substring(0, 32)->toString();
		}
		
		$email = trim($billing->getEMailAddress());
		if (empty($email)) {
			$email = trim($orderContext->getCustomerEMailAddress());
		}
		if (!empty($email)) {
			$parameters['email'] = Customweb_Core_String::_($email)->substring(0, 254)->toString();
		}
		
		return $parameters;
	}

	protected function getShippingParameters(Customweb_Payment_Authorization_IOrderContext $orderContext, Customweb_Payment_Authorization_IPaymentCustomerContext $paymentCustomerContext, array $formData){
		$shipping = $orderContext->getShippingAddress();
		$parameters = array();
		$name = trim($shipping->getFirstName() . ' ' . $shipping->getLastName());
		if (!empty($name)) {
			$parameters['sname'] = Customweb_Core_String::_($name)->substring(0, 96)->toString();
		}
		
		$street = trim($shipping->getStreet());
		if (!empty($street)) {
			$parameters['saddr1'] = Customweb_Core_String::_($street)->substring(0, 96)->toString();
		}
		
		$city = trim($shipping->getCity());
		if (!empty($city)) {
			$parameters['scity'] = Customweb_Core_String::_($city)->substring(0, 96)->toString();
		}
		$state = trim($shipping->getState());
		if (!empty($state)) {
			$parameters['sstate'] = Customweb_Core_String::_($state)->substring(0, 96)->toString();
		}
		$country = trim($shipping->getCountryIsoCode());
		if (!empty($country)) {
			$parameters['scountry'] = $country;
		}
		$zip = trim($shipping->getPostCode());
		if (!empty($zip)) {
			$parameters['szip'] = Customweb_Core_String::_($zip)->substring(0, 24)->toString();
		}
		return $parameters;
	}

