	public function isDeferredCapturingSupported(){
		return $this->existsPaymentMethodConfigurationValue('capturing');
	}

	public function getPaymentType(){
		$params = $this->getPaymentMethodParameters();
		return $params['paymentType'];
	}

	public function isAliasManagerSupported(){
		return !in_array('AliasManager', $this->getNotSupportedFeatures());
	}

	public function isAliasManagerActive(){
		return $this->getPaymentMethodConfigurationValue('alias_manager') == 'active';
	}

	public function isSolvencyCheckActive(){
		if (!$this->existsPaymentMethodConfigurationValue('solvency_service')) {
			return false;
		}
		return $this->getPaymentMethodConfigurationValue('solvency_service') == 'active';
	}

	public function getLineItemParameters($invoiceItems, $currency){
		return array();
	}

